import { Component } from '@angular/core';

@Component({
  selector: 'diya1',
  templateUrl: './diya1.component.html',
  styleUrls: ['./diya1.component.css']
})
export class Diya1Component {
  dicPrize = 35;
  dipAk = 'dipak'
  phila:any ={
    name:'Diya',
    lName:'Patel',
    saYs:'Jay Swaminarayan',
    fColor:'Pink',
    fNumber:15
  }
  philae=true
  checkMe(){
    this.philae= false
  }
  addMe(event:any){
 this.dipAk= event.target.value;
  }
}
<button (click)="checkMe()">diya</button>
<div [hidden]="philae">
    <h3> My Name is {{phila.name}} </h3>
    <h3> My Last Name Is {{phila.lName}} </h3>
    <h2> I want to say you {{phila.saYs}} </h2>
    <h3> My Favorite color is {{phila.fColor}} </h3>
    <h3> My Favorite number is {{phila.fNumber}} </h3>
</div>
<h2> {{(100-dicPrize)/2}}% </h2>
<button (click)="addMe()">Click</button>
<input (input)="dipAk" >
<h2 >Show here: {{dipAk}} </h2>
